--  DDL for View V_AD_ITEMS
--------------------------------------------------------

  CREATE OR REPLACE FORCE EDITIONABLE VIEW "ADMIN"."V_AD_ITEMS" ("AD_ITEM_ID", "ITEM_ID", "ITEM", "ORIGINAL_PRICE", "DISCOUNT_PRICE", "ITEM_IMAGE", "ITEM_LABEL") DEFAULT COLLATION "USING_NLS_COMP"  AS 
  select  ad.ad_item_id, i.item_id,i.item,  i.original_price, i.discount_price, i.item_image,il.item_label    
 from ad_item ad
 inner join item i on i.ad_item_id=ad.ad_item_id
 left join item_label il on  i.item_label_id = il.item_label_id 
 order by ad.customize_ad_item_order
;
--------------------------------------------------------
--  DDL for View V_ALL_SECTIONS
--------------------------------------------------------

  CREATE OR REPLACE FORCE EDITIONABLE VIEW "ADMIN"."V_ALL_SECTIONS" ("ITEM_ID", "SUBCATEGORY_ID", "SUBCATEGORY_ITEM_ID", "ITEM_COLLECTION_ID", "AD_ITEM_ID", "MAIN_SECTION", "CATEGORY", "SUBCATEGORY", "ITEM", "ORIGINAL_PRICE", "DISCOUNT_PRICE", "ITEM_LABEL", "ITEM_COLLECTION", "ITEM_IMAGE", "CUSTOMIZE_MAIN_SECTION_ORDER", "CUSTOMIZE_CATEGORY_ORDER", "CUSTOMIZE_SUBCATEGORY_ORDER") DEFAULT COLLATION "USING_NLS_COMP"  AS 
  SELECT DISTINCT i.item_id,scat.subcategory_id,scati.Subcategory_Item_id,ic.item_collection_id,i.ad_item_id,ms.main_section,cat.category,scat.subcategory,i.item,i.original_price,discount_price,
        il.item_label,ic.item_collection, i.item_image,
        ms.customize_main_section_order,cat.customize_category_order ,scat.customize_subcategory_order
 FROM  main_section ms   
 INNER JOIN category cat ON ms.main_section_id=cat.main_section_id
 INNER JOIN Subcategory scat On cat.category_id=scat.category_id
 Left JOIN Subcategory_Item scati On  scat.subcategory_id = scati.subcategory_id                                  
 Left Join item i on scati.item_id=i.item_id
 Left Join item_label il on i.item_label_id= il.item_label_id
 Left Join item_collection ic on i.item_collection_id=ic.item_collection_id
 --Where i.item_id=17
 ORDER BY  ms.customize_main_section_order,cat.customize_category_order ,scat.customize_subcategory_order
;
